//     width: 100%;
//     padding: 15px;
//     background-color: black;
//     color: white;
//     font-weight: 600;
// `

const Cart = () => {

    const cart = useSelector(state => state.cart)
    const [stripeToken, setStripeToken] = React.useState(null)

    //const dispatch = useDispatch()


    // const onToken = async (token) => {
    //     setStripeToken(token);
      
    //     if (cart.products.length > 0) {
    //       makeOrder();
    //     }
    //   };

    const onToken = async (token) => {
        setStripeToken(token);
      
        if (cart.products.length > 0) {
          const orderData = {
            userId: JSON.parse(JSON.parse(localStorage.getItem("persist:root")).user).currentUser._id,
            products: cart.products.map((product) => ({
              productId: product._id,
              quantity: product.quantity,
            })),
            amount: cart.total,
          };
      
          try {
            const res = await userRequest().post("/orders", orderData);
            console.log(res.data);
          } catch (error) {
            console.log(error);
          }
        }
      };

    // const handleDelete = (id) => {
    //     dispatch(deleteProduct(id))
    // }
    
    React.useEffect(() => {
        const makeRequest = async () => {
            try {
                const res = await userRequest.post("/checkout/payment", {
                    tokenId: stripeToken.id,
                    amount: cart.total * 100
                })
            } catch (error) {
                console.log(error)
            }
        }

        stripeToken && makeRequest() && window.location.replace('http://localhost:3000/success')

    }, [stripeToken, cart.total])


    // const makeOrder = async () => {

    //     const IdUser = JSON.parse(JSON.parse(localStorage.getItem("persist:root")).user).currentUser._id
        
