import { publicRequest } from "../requestMethods"
import { loginFailure, loginStart, loginSuccess } from "./userRedux"
import { registerFailure, registerStart, registerSuccess } from "./userRedux"

export const login = async (dispatch, user) => {
    dispatch(loginStart())
    try {
        const res = await publicRequest.post("/auth/login", user)
        dispatch(loginSuccess(res.data))
    } catch (error) {
        dispatch(loginFailure())
    }
}

export const register = async (dispatch, user) => {
    dispatch(registerStart())
    try {
        const res = await publicRequest.post("/auth/register", user)
        dispatch(registerSuccess(res.data))
    } catch (error) {
        dispatch(registerFailure())
    }
}

export const makeUserOrder = async (order) => {
    try {
      const res = await publicRequest.post("/users", order);
      return res.data;
    } catch (error) {
      console.log(error);
    }
  };

  export const deleteUserOrder = async (userId, orderId) => {
    try {
      const res = await publicRequest.delete(`/users/${userId}/orders/${orderId}`);
      return res.data;
    } catch (error) {
      console.log(error);
    }
  };

  export const getUserOrders = async () => {
    try {
      const res = await publicRequest.get("/users");
      return res.data;
    } catch (error) {
      console.log(error);
    }
  };
