const Hr = styled.hr`
    background-color: #eee;
    border: none;
    height: 5px;
`
const Summary = styled.div`
    flex: 1;
    border: 1px solid lightgray;
    border-radius: 10px;
    padding: 20px;
    height: 45vh;
`
const SummaryTitle = styled.h1`
    font-weight: 200;
`
const SummaryItem = styled.div`
    margin: 30px 0px;
    display: flex;
    justify-content: space-between;
    font-size: ${props => props.type === "total" && "24px"};
    font-weight: ${props => props.type === "total" && "600"};
`
const SummaryItemText = styled.div`
    
`
const SummaryItemPrice = styled.div`
    
`
const Button = styled.button`
    width: 100%;
    padding: 15px;
    background-color: black;
    color: white;
    font-weight: 600;
`

const Cart = () => {

    const cart = useSelector(state => state.cart)
    const [stripeToken, setStripeToken] = React.useState(null)

    const dispatch = useDispatch()

    const onToken = async (token) => {
        setStripeToken(token)
      };

    const handleDelete = (id) => {
        dispatch(deleteProduct(id))
    }
 
    const makeOrder = async () => {
        console.log("Executing makeOrder function")

        const IdUser = JSON.parse(JSON.parse(localStorage.getItem("persist:root")).user).currentUser._id
        const orderData = {
            userId: IdUser,
            products: cart.products.map((product) => ({
              productId: product._id,
              quantity: product.quantity,
              img: product.img,
              price: product.price,
            })),
            amount: cart.total,
            quantity: cart.quantity,
          }; 
          console.log(orderData)
        
        if (cart.products.length > 0) {
            try {
                await userRequest().post("/orders/", orderData)
                //await addUserOrder(orderData)
            } catch (error) {
                console.log(error)
            }
        }
    }

    const redirectToSuccessPage = () => {
        setTimeout(() => {
          window.location.replace('http://localhost:3000/success');
        }, 2000)
      };

    React.useEffect(() => {

        const makeRequest = async () => {
            try {
                const res = await userRequest.post("/checkout/payment", {
                    tokenId: stripeToken.id,
                    amount: cart.total * 100
                })
            } catch (error) {
                console.log(error)
            }
        }

        if (stripeToken) {
            makeRequest()
            dispatch(clearCart())
            redirectToSuccessPage()
          }

    }, [stripeToken, cart.total, dispatch])

    const [userOrders, setUserOrders] = React.useState([]);

    // React.useEffect(() => {

    //     const IdUser = JSON.parse(JSON.parse(localStorage.getItem("persist:root")).user).currentUser._id
    //     console.log("start execution")

    //     const fetchUserOrders = async () => {
    //         try {
    //             const orders = await getUserOrders(IdUser);
    //             console.log("middle of execution")
    //             setUserOrders(orders);
    //         } catch (error) {
    //             console.log(error);
    //         }
    //     };

    //     fetchUserOrders();
    // }, [userOrders]);

    React.useEffect(() => {
        const IdUser = JSON.parse(
          JSON.parse(localStorage.getItem("persist:root")).user
        ).currentUser._id;
    
        const fetchUserOrders = async () => {
          try {
            const orders = await getUserOrders(IdUser);
            setUserOrders(orders);
            //console.log("Fetched orders:", orders);
          } catch (error) {
            console.log(error);
          }
        };
    
        if (IdUser) {
            //console.log("Fetching user orders...");
            fetchUserOrders();
          }
      }, []);
    
      React.useEffect(() => {
        console.log("Here are the user orders", userOrders);
      }, [userOrders])


      const handleQuantityChange = (event, product) => {
        const newQuantity = Number(event.target.value);
        dispatch(updateQuantity({ productId: product._id, quantity: newQuantity }));
      };
    
      const handleIncrease = (product) => {
        const newQuantity = product.quantity + 1;
        dispatch(updateQuantity({ productId: product._id, quantity: newQuantity }));
      };
    
      const handleDecrease = (product) => {
        if (product.quantity > 1) {
          const newQuantity = product.quantity - 1;
          dispatch(updateQuantity({ productId: product._id, quantity: newQuantity }));
        }
      };



  return (
    <Container>
        <Navbar/>
        <Announcement/>
        <Wrapper>
            <Title>YOUR BAG</Title>
            <Top>
                <Link to="/home"> 
                    <TopButton>CONTINUE SHOPPING</TopButton>
                </Link>
                <TopTexts>
                    <TopText>Shopping Bag (2)</TopText>
                    <TopText>Your Wishlist (0)</TopText>
                </TopTexts>
                <TopButton type="filled">CHECKOUT NOW</TopButton>
            </Top>
            <Bottom>
                <Info>
                    {cart.products.map(product =>
                            <Product>
                                <ProductDetail>
                                    <Image src={product.img} />
                                    <Details>
                                        <ProductName><b>Product:</b> {product.title}</ProductName>
                                        <ProductId><b>ID:</b> {product._id}</ProductId>
                                        <ProductColor color={product.color} />
                                        <ProductSize><b>Size:</b> {product.size}</ProductSize>
                                    </Details>
                                </ProductDetail>
                                <PriceDetail>
                                    {/* <ProductAmmountContainer>
                                        <Remove/>
                                        <ProductAmmount>{product.quantity}</ProductAmmount>
                                        <Add/>
                                    </ProductAmmountContainer> */}
                                    <ProductAmmountContainer>
                                        <Remove onClick={() => handleDecrease(product)} />
                                        <ProductAmmount
                                            value={product.quantity}
                                            onChange={(event) => handleQuantityChange(event, product)}
                                        />
                                        <Add onClick={() => handleIncrease(product)} />
                                    </ProductAmmountContainer>
                                    <ProductPrice>${product.price * product.quantity}</ProductPrice>
                                    <Button onClick={() => handleDelete(product.productId)}>
                                        <DeleteForever/>
                                    </Button>
                                </PriceDetail>
                            </Product>
                        )
                    }
                    <Hr/>
                </Info>
                <Summary>
                    <SummaryTitle>ORDER SUMMARY</SummaryTitle>
                    <SummaryItem>
                        <SummaryItemText>Subtotal</SummaryItemText>
                        <SummaryItemPrice>${cart.total}</SummaryItemPrice>
                    </SummaryItem>
                    <SummaryItem>
                        <SummaryItemText>Estimated Shipping</SummaryItemText>
                        <SummaryItemPrice>$20.99</SummaryItemPrice>
                    </SummaryItem>
                    <SummaryItem>
                        <SummaryItemText>Shipping Discount</SummaryItemText>
                        <SummaryItemPrice>- $5.99</SummaryItemPrice>
                    </SummaryItem>
                    <SummaryItem type="total">
                        <SummaryItemText>Total</SummaryItemText>
                        <SummaryItemPrice>${cart.total}</SummaryItemPrice>
                    </SummaryItem>
                    <span onClick={makeOrder}>
                        <StripeCheckout
                            name="AGFirass"
                            image=''
                            billingAddress
                            shippingAddress
                            description={`Your total is $${cart.total}`}
                            amount={cart.total*100}
                            token={onToken}
                            stripeKey={KEY}
                        />
                    </span>
                </Summary>
            </Bottom>
        </Wrapper>
        <Newsletter/>
        <Footer/>
    </Container>
  )
}

export default Cart
