import React from 'react';
import styled from 'styled-components';
import {
  getStorage,
  ref,
  uploadBytesResumable,
  getDownloadURL,
} from 'firebase/storage';
import app from '../../firebase';
import { addSuggestedArticle } from '../../apiCalls';
import sidebarImg from '../../images/sidebarImg.png';

const SidebarWrapper = styled.div`
  background-color: #f7f7f7;
  padding: 20px;
  border-radius: 5px;
`;

const SidebarItem = styled.div`
  margin-bottom: 20px;

  img {
    width: 100%;
    margin-bottom: 10px;
  }

  p {
    font-size: 14px;
    line-height: 1.4;
    color: #555;
  }
`;

const SidebarTitle = styled.span`
  font-size: 16px;
  font-weight: bold;
  margin-bottom: 10px;
  display: block;
`;

const SidebarForm = styled.form``;

const SidebarFormItem = styled.div`
  margin-bottom: 10px;

  label {
    display: block;
    font-size: 14px;
    margin-bottom: 5px;
  }

  input,
  textarea {
    width: 100%;
    padding: 10px;
    border: 1px solid #ddd;
    border-radius: 5px;
  }

  textarea {
    resize: vertical;
  }
`;

const SidebarFormButton = styled.button`
  background-color: #e91e63;
  color: #fff;
  border: none;
  padding: 10px 15px;
  border-radius: 5px;
  cursor: pointer;
`;

const ErrorText = styled.p`
  color: red;
  margin-top: 10px;
`;

const Sidebar = () => {
  const [inputs, setInputs] = React.useState({});
  const [file, setFile] = React.useState(null);
  const [error, setError] = React.useState('');

  const handleChange = (e) => {
    setInputs((prev) => {
      return { ...prev, [e.target.name]: e.target.value };
    });
  };

  const handleClick = (e) => {
    e.preventDefault();
    if (!inputs.fullName || !inputs.email || !inputs.title || !inputs.desc || !file) {
      setError('Please fill in all fields and choose an image.');
      return;
    }

    const fileName = new Date().getTime() + file.name;
    const storage = getStorage(app);
    const storageRef = ref(storage, fileName);
    const uploadTask = uploadBytesResumable(storageRef, file);

    uploadTask.on(
      'state_changed',
      (snapshot) => {
        // Progress observer
        const progress = (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
        console.log('Upload is ' + progress + '% done');
        switch (snapshot.state) {
          case 'paused':
            console.log('Upload is paused');
            break;
          case 'running':
            console.log('Upload is running');
            break;
          default:
        }
      },
      (error) => {
        // Error observer
        console.log('Upload error:', error);
      },
      () => {
        // Completion observer
        getDownloadURL(uploadTask.snapshot.ref).then((downloadURL) => {
          const suggestedArticle = { ...inputs, img: downloadURL };
          addSuggestedArticle(suggestedArticle);
          setTimeout(() => {
            window.location.reload();
          }, 3000);
        });
      }
    );
  };

  return (
    <SidebarWrapper>
      <SidebarItem>
        <SidebarTitle>You got some news?</SidebarTitle>
        <img src={sidebarImg} alt="" />
        <p>
          We are happy to promote relevant accessibility related news stories to our GAMING community. If you have a
          story we should be aware of please let us know using the form below.
        </p>
      </SidebarItem>
      <SidebarItem>
        <SidebarTitle>SUGGEST NEWS</SidebarTitle>
        <SidebarForm>
          <SidebarFormItem>
            <label>Full Name</label>
            <input name="fullName" type="text" placeholder="Full Name" onChange={handleChange} />
          </SidebarFormItem>
          <SidebarFormItem>
            <label>Email</label>
            <input name="email" type="email" placeholder="Email" onChange={handleChange} />
          </SidebarFormItem>
          <SidebarFormItem>
            <label>Title</label>
            <input name="title" type="text" placeholder="Title" onChange={handleChange} />
          </SidebarFormItem>
          <SidebarFormItem>
            <label>Description</label>
            <textarea name="desc" rows="6" placeholder="Enter your news here..." onChange={handleChange} />
          </SidebarFormItem>
          <SidebarFormItem>
            <label>Image</label>
            <input type="file" id="file" onChange={(e) => setFile(e.target.files[0])} />
          </SidebarFormItem>
          {error && <ErrorText>{error}</ErrorText>}
          <SidebarFormButton onClick={handleClick}>Send</SidebarFormButton>
        </SidebarForm>
      </SidebarItem>
      <SidebarItem>
        <SidebarTitle>FOLLOW US</SidebarTitle>
        <div className="sidebarSocial">
          {/* Social media icons */}
        </div>
      </SidebarItem>
    </SidebarWrapper>
  );
};

export default Sidebar;
